import Container from "../components/general_components/Container";
import HeaderTienda from "../components/shop_components/HeaderTienda";
import FooterTienda from "../components/shop_components/FooterTienda";
import ContentCategoria from "../components/shop_components/ContentCategoria";
import ContentShop from "../components/shop_components/ContentShop";
import Title from "../components/general_components/Title";
import { motion } from "framer-motion";
import CategoriaItem from "../components/shop_components/CategoriaItem";
import ContentGeneric from "../components/shop_components/ContentGeneric";
import Scroll from "../components/general_components/Scroll";

const itemsTest = [
  {
    url: "/svgs/svgs_Fiesta.svg",
    name: "Ofertas",
  },
  {
    url: "/svgs/svgs_Zapato.svg",
    name: "Zapatos",
  },
  {
    url: "/svgs/svgs_Reloj.svg",
    name: "Relojes",
  },
  {
    url: "/svgs/svgs_Camiseta.svg",
    name: "Camisetas",
  },
  {
    url: "/svgs/svgs_Bolso.svg",
    name: "Bolsos",
  },
  {
    url: "/svgs/svgs_Niño.svg",
    name: "Niños",
  },
  {
    url: "/svgs/svgs_Joyas.svg",
    name: "Joyeria",
  },
  {
    url: "/svgs/svgs_Sofa.svg",
    name: "Hogar",
  },
  {
    url: "/svgs/svgs_Utiles.svg",
    name: "Random",
  },
];

const FirstPage = () => {
  return (
    <motion.div
      exit="pageExit"
      initial="initial"
      animate="animate"
      variants={{
        pageExit: {
          opacity: 0,
        },
        animate: {
          opacity: 1,
          transition: {
            duration: 0.6,
          },
        },
      }}
    >
      <HeaderTienda imageUrl="/images/foto3.jpg">
        <Title
          titulo="ShopAR"
          descripcion="¡Selecciona tu tienda favorita y comienza a experimentar la
        realidad aumentada!"
        />
      </HeaderTienda>
      <Container title="ShopAR - Tienda">
        <div className="row">
          <div className="col-12">
            <div className="row">
              <ContentCategoria>
                {itemsTest.map((item, index) => {
                  return (
                    <CategoriaItem
                      categoryUrl={item.url}
                      categoryName={item.name}
                      categoryAlt={item.name}
                    />
                  );
                })}
              </ContentCategoria>
              <ContentShop name="Bershka">
                <ContentGeneric />
                <Scroll />
              </ContentShop>
            </div>
          </div>
        </div>
      </Container>
      <FooterTienda href="/" />
    </motion.div>
  );
};

export default FirstPage;
