import { firestore } from "../../firebase/admin";

const dataItems = {
  description: "Bola de billar que representa el poder de la realidad aumentada",
  imageAlt: "Bola de billar",
  imageUrl: "/images/ball.jpg",
  title: "Bola de billar",
};

export default (req, res) => {
  const { query } = req;
  const { category } = query;

  firestore
    .collection("categorias")
    .doc(category)
    .collection("items")
    .add(dataItems)
    .catch((e) => {
      console.log(e);
    });
  res.status(200).json({ Info: "Subido correctamente" });
};
