import { firestore } from "../../firebase/admin";

export default (req, res) => {
  firestore
    .collection("categorias")
    .get()
    .then((doc) => {
      const data = doc.data();
      res.json(data);
    })
    .catch(() => {
      Response.status(404).end();
    });
};
