import Container from "../components/general_components/Container";
import HeaderTienda from "../components/shop_components/HeaderTienda";
import FooterTienda from "../components/shop_components/FooterTienda";
import ContentCategoria from "../components/shop_components/ContentCategoria";
import ContentShop from "../components/shop_components/ContentShop";
import ContentItem from "../components/shop_components/ContentItem";
import Title from "../components/general_components/Title";
import { motion } from "framer-motion";
import CategoriaItem from "../components/shop_components/CategoriaItem";

const itemsTest = [
  "Item 1",
  "Item 2",
  "Item 3",
  "Item 4",
  "Item 5",
  "Item 2",
  "Item 3",
  "Item 4",
  "Item 5",
];
const contentItem = [
  {
    talla: "XL",
    precio: "$3.50",
    agregadoHace: "Agregado hace 1 mes",
  },
  {
    talla: "XL",
    precio: "$3.50",
    agregadoHace: "Agregado hace 1 mes",
  },
  {
    talla: "XL",
    precio: "$3.50",
    agregadoHace: "Agregado hace 1 mes",
  },
  {
    talla: "XL",
    precio: "$3.50",
    agregadoHace: "Agregado hace 1 mes",
  },
];

const FirstPage = () => {
  return (
    <motion.div
      exit="pageExit"
      initial="initial"
      animate="animate"
      variants={{
        pageExit: {
          opacity: 0,
        },
        animate: {
          opacity: 1,
          transition: {
            duration: 0.6,
          },
        },
      }}
    >
      <HeaderTienda>
        <Title
          titulo="ShopAR"
          descripcion="Â¡Selecciona tu tienda favorita y comienza a experimentar la
        realidad aumentada!"
        />
      </HeaderTienda>
      <Container title="ShopAR - Tienda">
        <div className="row">
          <div className="col-12">
            <div className="row">
              <ContentCategoria>
                {itemsTest.map((item, index) => {
                  return <CategoriaItem />;
                })}
              </ContentCategoria>
              <ContentShop>
                {contentItem.map((item) => {
                  return (
                    <ContentItem
                      talla={item.talla}
                      precio={item.precio}
                      agregadoHace={item.agregadoHace}
                    />
                  );
                })}
              </ContentShop>
            </div>
          </div>
        </div>
      </Container>
      <FooterTienda href="/" />
    </motion.div>
  );
};

export default FirstPage;
