import { firestore } from "../../firebase/admin";

export default (req, res) => {
  const { query } = req;
  const { id } = query;

  firestore
    .collection("items")
    .doc(id)
    .get()
    .then((doc) => {
      const data = doc.data();
      res.json(data);
    });
};
