import { AnimatePresence, animatePresence, motion } from "framer-motion";

const Modal = ({ isToggled, children, seItemToggled }) => {
  return (
    <AnimatePresence>
      {isToggled && (
        <>
          <div>
            <button onClick={() => seItemToggled(false)}>Close</button>
            {children}
          </div>
        </>
      )}
    </AnimatePresence>
  );
};

export default Modal;
