import Slider from "react-slick";

const Banner = () => {
  var settings = {
    className: "slider variable-width",
    swipe: false,
    arrows: false,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoPlaySpeed: 1000,
    autoplay: true,
    adaptiveHeight: true,
    fade: true,
  };
  return (
    <Slider {...settings}>
      <div style={{ width: 3000 }}>
        <img src="/images/foto1.jpg" alt="foto1" />
      </div>
      <div style={{ width: 400 }}>
        <img src="/images/foto2.jpg" alt="foto2" />
      </div>
      <div style={{ width: 1000 }}>
        <img src="/images/foto3.jpg" alt="foto3" />
      </div>
    </Slider>
  );
};

export default Banner;
