import Slider from "react-slick";

const Banner = () => {
  var settings = {
    className: "slider variable-width imagen",
    swipe: false,
    arrows: false,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoPlaySpeed: 1000,
    autoplay: true,
    adaptiveHeight: true,
    fade: true,
  };
  return (
    <Slider {...settings}>
      <div style={{ width: "100px" }}>
        <img className="imagen" src="/images/foto1.jpg" alt="foto1" />
      </div>
      <div style={{ width: 100 }}>
        <img className="imagen" src="/images/foto2.jpg" alt="foto2" />
      </div>
      <div style={{ width: 100 }}>
        <img className="imagen" src="/images/foto3.jpg" alt="foto3" />
      </div>
    </Slider>
  );
};

export default Banner;
