import { AnimatePresence, animatePresence, motion } from "framer-motion";

const Modal = ({ isToggled, children, setItemToggled }) => {
  return (
    <AnimatePresence>
      {isToggled && (
        <>
          <div>
            <button onClick={() => setItemToggled(false)}>Close</button>
            {children}
          </div>
        </>
      )}
    </AnimatePresence>
  );
};

export default Modal;
