const ItemModal = ({
  closeModal,
  imgModal,
  productName,
  description,
  stars,
  price,
}) => {
  return (
    <div className="card mt-4">
      <img className="card-img-top img-fluid index-menu" src={imgModal} />
      <div className="card-body">
        <h3 className="card-title">{productName}</h3>
        <h4>${price}</h4>
        <p className="card-text">{description}</p>
        <span className="text-warning">
          {stars == null ? "★ ★ ★ ★ ☆" : stars}
        </span>
        <p>{stars == null ? "4.0 stars" : "4.0 stars"}</p>
        <hr />
        <button className="btn btn-primary btn-block" onClick={closeModal}>
          ¡Visualizalo en Realidad Aumentada!
        </button>
        <button className="btn btn-alert btn-block" onClick={closeModal}>
          Regresar
        </button>
      </div>
    </div>
  );
};

export default ItemModal;
