import { motion } from "framer-motion";

const easing = [0.6, -0.05, 0.01, 0.99];

const fadeInUp = {
  initial: {
    y: 60,
    opacity: 0,
  },
  animate: {
    y: 0,
    opacity: 1,
    transition: {
      duration: 0.6,
      ease: easing,
    },
  },
};

const ContentItem = (props) => {
  return (
    <motion.div variants={fadeInUp} className="col-md-6 col-xl-4 mb-5">
      <a className="card lift h-100" href="#!">
        <div className="card-flag card-flag-dark card-flag-top-right">
          {props.agregadoHace}
        </div>
        <div>
          <img
            className="card-img-top items"
            src="https://source.unsplash.com/random"
            alt="..."
          />
          <div className="overlay"></div>
        </div>
        <div className="top-left">
          <h3 className="text-primary mb-0">{props.precio}</h3>
          <div className="small text-gray-800 font-weight-500">
            {props.talla}
          </div>
        </div>
        <div className="centered-2">
          <button
            class="btn btn-primary btn-block btn-marketing rounded-pill"
            type="submit"
          >
            Realidad aumentada
          </button>
        </div>
      </a>
    </motion.div>
  );
};

export default ContentItem;
