import HeaderTienda from "../components/shop_components/HeaderTienda";
import FooterTienda from "../components/shop_components/FooterTienda";
import ContentCategoria from "../components/shop_components/ContentCategoria";
import ContentShop from "../components/shop_components/ContentShop";
import Title from "../components/general_components/Title";
import CategoriaItem from "../components/shop_components/CategoriaItem";
import CardComponent from "../components/general_components/CardComponent";
import BiggerCardComponent from "../components/general_components/BiggerCardComponent";

import { motion } from "framer-motion";
import { useEffect, useState } from "react";

const itemsTest = [
  {
    url: "/svgs/svgs_Fiesta.svg",
    name: "Ofertas",
    color: "#ac62e8",
  },
  {
    url: "/svgs/svgs_Zapato.svg",
    name: "Zapatos",
    color: "#f76400",
  },
  {
    url: "/svgs/svgs_Reloj.svg",
    name: "Relojes",
    color: "#e30059",
  },
  {
    url: "/svgs/svgs_Camiseta.svg",
    name: "Camisetas",
    color: "#f4a100",
  },
  {
    url: "/svgs/svgs_Bolso.svg",
    name: "Bolsos",
    color: "#25e070",
  },
  {
    url: "/svgs/svgs_Niño.svg",
    name: "Niños",
    color: "#89d2c8",
  },
  {
    url: "/svgs/svgs_Joyas.svg",
    name: "Joyeria",
    color: "#b7d45d",
  },
  {
    url: "/svgs/svgs_Sofa.svg",
    name: "Hogar",
    color: "#2752bd",
  },
  {
    url: "/svgs/svgs_Utiles.svg",
    name: "Random",
    color: "#0c5c6b",
  },
];

const fakeData = [1, 2, 3, 4];

const FirstPage = () => {
  const [categoryClicked, setCategoryClicked] = useState(false);

  const renderDataCategory = () =>{

  }

  return (
    <motion.div
      exit="pageExit"
      initial="initial"
      animate="animate"
      variants={{
        pageExit: {
          opacity: 0,
        },
        animate: {
          opacity: 1,
          transition: {
            duration: 0.6,
          },
        },
      }}
    >
      <HeaderTienda imageUrl="/images/foto3.jpg">
        <Title
          titulo="ShopAR"
          descripcion="¡Selecciona tu tienda favorita y comienza a experimentar la
        realidad aumentada!"
        />
      </HeaderTienda>
      <Container title="ShopAR - Tienda">
        <button
          onClick={() => setCategoryClicked((prevValue) => !prevValue)}
        ></button>
        <div className="row">
          <div className="col-12">
            <div className="row">
              <ContentCategoria>
                {itemsTest.map((item) => {
                  return (
                    <CategoriaItem
                      categoryUrl={item.url}
                      categoryName={item.name}
                      categoryAlt={item.name}
                      color={item.color}
                    />
                  );
                })}
              </ContentCategoria>
              <ContentShop name="Bershka">
                {categoryClicked ? (
                  fakeData.map((item) => {
                    return (
                      <CardComponent
                        imageUrl="https://source.unsplash.com/random"
                        imageAlt="Al"
                        title="Element"
                        description="Element 1 description"

                      />
                    );
                  })
                ) : (
                  <BiggerCardComponent
                    imageUrl="https://source.unsplash.com/vZJdYl5JVXY/660x360"
                    title="Bigger component title"
                    description="bigger component description"
                  />
                )}
              </ContentShop>
            </div>
          </div>
        </div>
      </Container>
      <FooterTienda href="/" />
    </motion.div>
  );
};

export default FirstPage;
