import { motion } from "framer-motion";
import { useEffect, useState } from "react";

const easing = [0.6, -0.05, 0.01, 0.99];

const fadeInRight = {
  initial: {
    x: -60,
    opacity: 0,
  },
  animate: {
    x: 0,
    opacity: 1,
    transition: {
      duration: 0.6,
      ease: easing,
    },
  },
};

const randomColors = [
  "#ac62e8",
  "#0c5c6b",
  "#2752bd",
  "#b7d45d",
  "#89d2c8",
  "#25e070",
  "#11920b",
  "#0ec247",
  "#de5ad8",
  "#426af9",
  "#f4a100",
  "#f76400",
  "#e30059",
  "#00ba94",
  "#323f52",
];

const CategoriaItem = (props) => {
  const [isSelected, setSelected] = useState(false);
  const color = randomColors[Math.floor(Math.random() * randomColors.length)];
  let colorVariable = color;

  function imprimirEstado() {
    console.log(isSelected);
    setSelected((prevValue) => !prevValue);
  }

  useEffect(() => {
    if (isSelected == true) {
      colorVariable = "#2752bd";
    } else {
      colorVariable = color;
    }
  });

  return (
    <motion.div
      variants={fadeInRight}
      className="col-12 mt-0 mb-3 pr-3 pl-2 pt-2"
    >
      <a
        className="card lift pb-5 pt-2"
        style={{
          backgroundColor: colorVariable,
          cursor: "pointer",
        }}
        onClick={imprimirEstado}
      >
        <img
          className="card-img-top categories"
          src={props.categoryUrl}
          alt={props.categoryAlt}
        />
        <div className="centered">
          <h3 style={{ color: "white" }}>{props.categoryName}</h3>
        </div>
      </a>
    </motion.div>
  );
};

export default CategoriaItem;
