import { AnimatePresence, motion } from "framer-motion";

const Modal = ({ itemToggled, children, setItemToggled }) => {
  return (
    <AnimatePresence>
      {itemToggled && (
        <>
          <div>
            <button
              className="btn btn-danger"
              onClick={() => {
                console.log("clickeado");
                setItemToggled(false);
              }}
            >
              Close
            </button>
            {children}
          </div>
        </>
      )}
    </AnimatePresence>
  );
};

export default Modal;
