import { useState } from "react";

const ItemModal = ({
  closeModal,
  imgModal,
  productName,
  description,
  stars,
  price,
}) => {
  return (
    <div className="card post-preview lift mt-4">
      <img className="card-img-top img-fluid index-menu" src={imgModal} />
      <div className="card-body">
        <h3 className="card-title">{productName}</h3>
        <h4>${price}</h4>
        <p className="card-text">{description}</p>
        <span className="text-warning">
          {stars == null ? "★ ★ ★ ★ ☆" : stars}
        </span>
        <p>{stars == null ? "4.0 stars" : "4.0 stars"}</p>
      </div>
      <div className="card-footer">
        <a data-8code="6rfgc"></a>
        <div className="post-preview-meta">
          <img className="post-preview-meta-img" src="/images/ar.png" />
          <div className="post-preview-meta-details">
            <div className="post-preview-meta-details-name">
              ¡Realidad Aumentada!
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ItemModal;
