import { firestore } from "../../firebase/admin";

async function getShops(req, res) {
  firestore
    .collection("shops")
    .get()
    .then((doc) => {
      const data = doc.data();
      res.json(data);
    })
    .catch(() => {
      Response.status(404).end();
    });
}

export default getShops;
