import { motion } from "framer-motion";
import Modal from "react-modal";
import { useState } from "react";
import BiggerCardComponent from "../../general_components/card/BiggerCardComponent";

const easing = [0.6, -0.05, 0.01, 0.99];

const fadeInUp = {
  initial: {
    y: 60,
    opacity: 0,
  },
  animate: {
    y: 0,
    opacity: 1,
    transition: {
      duration: 0.6,
      ease: easing,
    },
  },
};
const customStyles = {
  content: {
    top: "30%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};
Modal.setAppElement("#__next");

const CardComponent = ({ imageUrl, imageAlt, title, description }) => {
  const [modalIsOpen, setIsOpen] = useState(false);
  function closeModal() {
    setIsOpen(false);
  }
  function openModal() {
    setIsOpen(true);
  }
  return (
    <>
      <motion.div variants={fadeInUp} className="col-md-6 col-xl-4 mb-5">
        <a
          className="card post-preview lift h-100"
          href="#!"
          onClick={openModal}
        >
          <img className="card-img-top items" src={imageUrl} alt={imageAlt} />
          <div className="card-body">
            <h5 className="card-title">{title}</h5>
            <p className="card-text">{description}</p>
          </div>
          <div className="card-footer">
            <div className="post-preview-meta">
              <img className="post-preview-meta-img" src="/images/ar.png" />
              <div className="post-preview-meta-details">
                <div className="post-preview-meta-details-name">
                  Â¡Realidad Aumentada!
                </div>
              </div>
            </div>
          </div>
        </a>
      </motion.div>
      {/* <Modal isOpen={modalIsOpen} style={customStyles}>
        <BiggerCardComponent />
        <button className="btn btn-primary" onClick={closeModal}>
          close
        </button>
      </Modal> */}
    </>
  );
};

export default CardComponent;
